import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import styled from 'styled-components';
import { mobile, tablet, tabletLarge } from '../responsive';


const Container = styled.div`
  flex: 1;
  min-width: 280px;;
  margin: 3px;
  height: 70vh;
  position: relative;
  display: flex;
  justify-content: center;
  background-color: #f5fbfd;
  ${tabletLarge({
    height: '60vh',
  })};
  ${mobile({ height: "20vh "})}
`;

const Image = styled.img`
  height: 100%;
  width: 100%;
  object-fit: cover;
`;

const Info = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Title = styled.h1`
  color: white;
  margin-top: 0;
  margin-bottom: 10px;
  font-weight: 600;
  ${tabletLarge({
    fontSize: '28px'
  })};
  ${mobile({
    fontSize: '20px'
  })}
`;

const Button = styled.button`
  border: none;
  width: 300px;
  padding: 10px;
  background-color: white;
  color: black;
  cursor: pointer;
  font-size: 20px;
  font-weight: 300;
  &:hover{
    background-color: pink;
  }
  ${tabletLarge({
    fontSize: '16px',
    width: '250px',
  })};
  ${tablet({
    fontSize: '14px',
    width: '220px'
  })};
  ${mobile({
    fontSize: '10px',
    width: '150px'
  })}
`;

const CategoriesItem = ({ item }) => {
    const { t } = useTranslation(["slider"]);

  return (
    <Container>
        <Link to={`/products/${item.category}`}>
            <Image src={item.img}/>
            <Info>
                <Title>{item.title}</Title>
                <Button>{t("shop_now")}</Button>
            </Info>
        </Link>
    </Container>
  )
}

export default CategoriesItem
