import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import ShoppingCartOutlinedIcon from '@mui/icons-material/ShoppingCartOutlined';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import FavoriteBorderOutlinedIcon from '@mui/icons-material/FavoriteBorderOutlined';
import FavoriteIcon from '@mui/icons-material/Favorite';
import { addProduct } from '../redux/wishlistRedux';
import { useDispatch } from 'react-redux';



const Info = styled.div`
  opacity: 0;
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  background-color: rgba(0,0,0,0.2);
  z-index: 3;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.5s ease;
  cursor: pointer;
`

const Container = styled.div`
  flex: 1;
  margin: 5px;
  min-width: 280px;
  height: 350px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #f5fbfd;
  position: relative;

  &:hover ${Info}{
    opacity: 1;
  }
`

const Circle = styled.div`
  width: 330px;
  height: 330px;
  border-radius: 50%;
  background-color: white;
  position: absolute;
`

const Image = styled.img`
  height: 75%;
  width: 75%;
  object-fit: cover;
  z-index: 2;
`

const Icon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 10px;
  transition: all 0.5s ease;
  &:hover{
    background-color: #e9f5f5;
    transform: scale(1.1);
  }
`;

const Title = styled.h3`
  position: absolute;
  top: -10px;
  z-index: 3;
`;


const Product = ({item}) => {
  const [isActive, setIsActive] = useState(true);
  const [product, setProduct] = useState({});
  const [quantity, setQuantity] = useState(100);
  const [color, setColor] = useState('red');
  const dispatch = useDispatch();
  const id = item._id;

  useEffect(() => {
      (async () => {
          try {
              setIsLoading(true);
              const res = await publicRequest.get('/products/find/' + id)
              setProduct(res.data)
          } catch(err) {
              console.log(err);
          } finally {
              setIsLoading(false);
          }
      })();
  }, [id]);

  const handleClick = () => {
      setIsActive(!isActive);
      dispatch(
          addProduct({ ...product, quantity, color })
      );
  };

  return (
    <Container>
      <Title>{item.title} - UAH {item.price}/kg</Title>
      <Circle />
      <Image src={item.img}/>
      <Info>
        <Icon>
          <Link to="/cart">
            <ShoppingCartOutlinedIcon />
          </Link>
        </Icon>
        <Icon>
          <Link to={`/product/${item._id}`}>
            <SearchOutlinedIcon />
          </Link>
        </Icon>
        <Icon>
          {isActive ? <FavoriteBorderOutlinedIcon onClick={() => handleClick()} />:
          <FavoriteIcon style={{ color: "red" }} onClick={() => handleClick()}/>}
        </Icon>
      </Info>
    </Container>
  )
}

export default Product
