import React from 'react';
import styled from 'styled-components';
import StripeCheckout from 'react-stripe-checkout';

const KEY = 'pk_test_51JMFblDfGs0uOkNS8Rvo7RjNahFl0Q49EYNxSlc1eQgMCU8emwodNF2BVhZ44eZIcoqMYwxSAlHvEBOUrrmmp0T400SCifnCWv'

const Container = styled.div`
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
`;

const Button = styled.button`
    border: none;
    width: 120;
    border-radius: 5;
    padding: 20px;
    background-color: black;
    color: white;
    font-weight: 600;
    cursor: pointer;
`;

const Pay = () => {

    const onToken = (token) => {
        console.log(token);
    }
  return (
    <Container>
        <StripeCheckout 
            name='Vinogradnik Bessarabii'
            image='../sources/images/grape-icon.png'
            billingAddress
            shippingAddress
            description='Your total is UAH 14000'
            amount={1400000}
            token={onToken}
            stripeKey={KEY}
        >
            <Button>Pay Now</Button>
        </StripeCheckout>
    </Container>
  )
}

export default Pay;
