import styled from "styled-components";
import FacebookIcon from '@mui/icons-material/Facebook';
import InstagramIcon from '@mui/icons-material/Instagram';
import TwitterIcon from '@mui/icons-material/Twitter';
import PinterestIcon from '@mui/icons-material/Pinterest';
// import PlaceIcon from '@mui/icons-material/Place';
import PhoneIcon from '@mui/icons-material/Phone';
import EmailIcon from '@mui/icons-material/Email';
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";


const Container = styled.div`

`;

const Left = styled.div`

`;

const Logo = styled.h1`

`;

const Center = styled.div`

`;

const Right = styled.div`

`;

const SocialIcon = styled.div`
    width: 40px;
    height: 40px;
    border-radius: 50%;
    color: white;
    background-color: #${props => props.color};
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 20px;
`;

const Footer = () => {
    const { t } = useTranslation(["footer"]);

  return (
    <Container>
      <Left>
        <Logo>{t("logo")}</Logo>
        <Desc>{t("desc")}</Desc>
        <SocialContainer>
            <SocialIcon color='3B5999'>
                <FacebookIcon />
            </SocialIcon>
            <SocialIcon color='E4405F'>
                <InstagramIcon />
            </SocialIcon>
            <SocialIcon color='55ACEE'>
                <TwitterIcon />
            </SocialIcon>
            <SocialIcon color='E60023'>
                <PinterestIcon />
            </SocialIcon>
        </SocialContainer>
      </Left>
      <Center>
        <h3>{t("links")}</h3>
        <ul>
            <li>
                <Link to="/" style={{ textDecoration: 'none', color: 'black' }}>
                    {t("home")}
                </Link>
            </li>
            <li>
                <Link to="/cart" style={{ textDecoration: 'none', color: 'black' }}>
                    {t("cart")}
                </Link>
            </li>
            <li>
                <Link to="/" style={{ textDecoration: 'none', color: 'black' }}>
                    {t("products")}
                </Link>
            </li>
            <li>{t("my_account")}</li>
            <li>{t("order_tracking")}</li>
            <li>{t("wishlist")}</li>
            <li>{t("terms")}</li>
        </ul>
      </Center>
      <Right>
        <h3>{t("contact")}</h3>
        <Link 
            to="/contact_us"
            style={{
                textDecoration: 'none',
                color: 'black',
            }}
        >
            Click here to send us a message
        </Link>
        {/* <ContactItem><PlaceIcon style={{ marginRight: '10px'}}/>{t("address")}</ContactItem> */}
        <div><PhoneIcon style={{ marginRight: '10px'}}/> +38 (096) 104 07 13</div>
        <div><EmailIcon style={{ marginRight: '10px'}}/> ivanzlatoff@gmail.com </div>
        <img src='https://i.ibb.co/Qfvn4z6/payment.png'/>
      </Right>
    </Container>
  )
}

export default Footer
