import axios from "axios";
import { publicRequest, userRequest } from "../requestMethods";
import { clearCart, fetchCart } from "./cartRedux";
import { 
    loginFailure, 
    loginStart, 
    loginSuccess, 
    refreshTokenStart,
    refreshTokenSuccess,
    refreshTokenFailure,
    logoutStart, 
    logoutSuccess, 
    logoutFailure 
} from "./userRedux"
import { clearWishlist, fetchWishlist } from "./wishlistRedux";
import { toast } from 'react-hot-toast';

export const refreshToken = async (dispatch, user) => {
    // dispatch(refreshTokenStart())
    try {
        const res = await axios.post("/refresh_token", { token: user.refreshToken });
        dispatch(refreshTokenSuccess(res.data));
        return res.data
    } catch(err) {
        // dispatch(refreshTokenFailure());
    }
};

export const login = async (dispatch, user) => {
    dispatch(loginStart());
    try {
        const res = await publicRequest.post("/auth/login", user)
        dispatch(loginSuccess(res.data))
        dispatch(fetchCart(res.data._id))
        dispatch(fetchWishlist(res.data._id))
    } catch(err) {
        dispatch(loginFailure());
        toast.error(err.response.data.error);
    }
};

export const logout = async (dispatch, user) => {
    dispatch(logoutStart());
    try {
        const res = await userRequest.delete("/auth/logout", user);
        if (res) {
            dispatch(logoutSuccess());
            dispatch(clearCart());
            dispatch(clearWishlist());
            localStorage.clear();
        } 
    } catch(err) {
        console.log(err);
        dispatch(logoutFailure());
    }
};
