import axios from 'axios';

const BASE_URL = 'http://localhost:5000/api/';
const TOKEN = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzYTg1MDUzMjU3NTljNDI4ZmIwNzExNyIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY3MjY5MDE3OCwiZXhwIjoxNjcyNjkxMDc4fQ.5hHp90sFFpBQO9mMMz-Vq9Kbzkbj7objtvSo2LGj4B4'
// const TOKEN = JSON.parse(JSON.parse(localStorage?.getItem("persist:root"))?.user)?.currentUser?.accessToken;
export const publicRequest = axios.create({
    baseURL: BASE_URL
});

export const userRequest = axios.create({
    baseURL: BASE_URL,
    headers: {token: `Bearer ${TOKEN}`},
});
