import axios from 'axios';

const BASE_URL = 'http://localhost:5000/api/';
// const TOKEN = JSON.parse(JSON.parse(localStorage?.getItem("persist:root"))?.user)?.currentUser?.accessToken;
const TOKEN = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzYTg1MDUzMjU3NTljNDI4ZmIwNzExNyIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY3NDY3NzI3MiwiZXhwIjoxNjc0Njc5MDcyfQ.zLnhpzhuOIb2fduuDludurpB5v0z3aDNFJR4trHy-0g"
export const publicRequest = axios.create({
    baseURL: BASE_URL
});

export const userRequest = axios.create({
    baseURL: BASE_URL,
    headers: {token: `Bearer ${TOKEN}`},
});
