import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import { publicRequest, userRequest } from "../requestMethods";


export const fetchCart = createAsyncThunk('cart/fetchCart', async (userId) => {
    console.log(userId)
    const res = await userRequest.get(`carts/find/${userId}`);
    console.log("products => ", res)
    return res.data.products;
});

const cartSlice = createSlice({
    name: 'cart',
    initialState: {
        products: [],
        quantity: 0,
        totalPrice: 0,
        status: 'idle',
        error: null     
    },
    reducers: {
        addProduct: (state, action) => {
            if (state.products?.length > 0) {
                for (let i = 0; i < state.products.length; i++) {
                    if (state.products[i]['_id'] === action.payload._id && state.products[i]['color'] === action.payload.color) {
                        state.products[i].quantity += action.payload.quantity;
                        state.totalPrice += action.payload.price * action.payload.quantity;
                        return
                    } 
                }
                state.quantity += 1;
                state.products.push(action.payload);
                state.totalPrice += action.payload.price * action.payload.quantity;               
            } else {
                state.quantity += 1;
                state.products.push(action.payload);
                state.totalPrice += action.payload.price * action.payload.quantity;
            }
        },
        increaseProduct: (state, action) => {
            if (state.products.length > 0) {
                for (let i = 0; i < state.products.length; i++) {
                    if (state.products[i]['_id'] === action.payload._id && state.products[i]['color'] === action.payload.color) {
                        if (state.products[i].quantity < 10000) {
                            state.products[i].quantity += 100;
                            state.totalPrice += action.payload.price * 100;
                        }
                        return                        
                    }
                }
            }
        },
        decreaseProduct: (state, action) => {
            if (state.products.length > 0) {
                for (let i = 0; i < state.products.length; i++) {
                    if (state.products[i]['_id'] === action.payload._id && state.products[i]['color'] === action.payload.color) {
                        if (state.products[i].quantity > 100) {
                            state.products[i].quantity -= 100;
                            state.totalPrice -= action.payload.price * 100;
                        }
                        return                        
                    }
                }
            }
        },
        deleteProduct: (state, action) => {
            console.log(state)
            if (state.products.length > 0) {
                for (let i = 0; i < state.products.length; i++) {
                    if (state.products[i]['_id'] === action.payload._id && state.products[i]['color'] === action.payload.color) {
                        state.totalPrice -= state.products[i].price*state.products[i].quantity;
                        state.products.splice(i, 1);
                        state.quantity -= 1;    
                        return
                    } 
                }        
            }
        },
        clearCart: (state) => {
            state.products = [];
            state.quantity = 0;
            state.totalPrice = 0;
            state.status = 'idle';
            state.error = null;
        },
    },
    extraReducers(builder) {
        builder
            .addCase(fetchCart.pending, (state, action) => {
                state.status = 'loading';
            })
            .addCase(fetchCart.fulfilled, (state, action) => {
                console.log("action => ", action)
                state.status = 'succeded';
                state.cart.products.push(...action.payload);
                state.cart.quantity = state.cart.products.length();
                state.cart.totalPrice = state.cart.products.reduce((a, b) => a['price']*a['quantity'] + b['price']*b['quantity'], 0)
            })
            .addCase(fetchCart.rejected, (state, action) => {
                state.cart.status = 'failed';
                state.cart.error = action.error.message;
            })
    },
});

export const { addProduct, increaseProduct, decreaseProduct, deleteProduct, clearCart } = cartSlice.actions;
export default cartSlice.reducer;
