import styled from "styled-components";
import FacebookIcon from '@mui/icons-material/Facebook';
import InstagramIcon from '@mui/icons-material/Instagram';
import TwitterIcon from '@mui/icons-material/Twitter';
import PinterestIcon from '@mui/icons-material/Pinterest';
// import PlaceIcon from '@mui/icons-material/Place';
import PhoneIcon from '@mui/icons-material/Phone';
import EmailIcon from '@mui/icons-material/Email';
import { mobile } from "../../responsive";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";
import './footer.css';


const SocialIcon = styled.div`
    width: 40px;
    height: 40px;
    border-radius: 50%;
    color: white;
    background-color: #${props => props.color};
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 20px;
`;

const ContactItem = styled.div`
    margin-bottom: 20px;
    margin-top: 20px;
    display: flex;
    align-items: center;
`;

const Payment = styled.img`
    width: 50%;
`

const Footer = () => {
    const { t } = useTranslation(["footer"]);

  return (
    <div className="container">
      <div className="left">
        <h1>{t("logo")}</h1>
        <p className="desc">{t("desc")}</p>
        <div className="socialContainer">
            <SocialIcon color='3B5999'>
                <FacebookIcon />
            </SocialIcon>
            <SocialIcon color='E4405F'>
                <InstagramIcon />
            </SocialIcon>
            <SocialIcon color='55ACEE'>
                <TwitterIcon />
            </SocialIcon>
            <SocialIcon color='E60023'>
                <PinterestIcon />
            </SocialIcon>
        </div>
      </div>
      <div className="center">
        <h3>{t("links")}</h3>
        <ul className="list">
            <li className="listItem">
                <Link to="/" style={{ textDecoration: 'none', color: 'black' }}>
                    {t("home")}
                </Link>
            </li>
            <li className="listItem">
                <Link to="/cart" style={{ textDecoration: 'none', color: 'black' }}>
                    {t("cart")}
                </Link>
            </li>
            <li className="listItem">
                <Link to="/" style={{ textDecoration: 'none', color: 'black' }}>
                    {t("products")}
                </Link>
            </li>
            <li className="listItem">{t("my_account")}</li>
            <li className="listItem">{t("order_tracking")}</li>
            <li className="listItem">{t("wishlist")}</li>
            <li className="listItem">{t("terms")}</li>
        </ul>
      </div>
      <div className="right">
        <h3>{t("contact")}</h3>
        <Link 
            to="/contact_us"
            style={{
                textDecoration: 'none',
                color: 'black',
            }}
        >
            Click here to send us a message
        </Link>
        {/* <ContactItem><PlaceIcon style={{ marginRight: '10px'}}/>{t("address")}</ContactItem> */}
        <ContactItem><PhoneIcon style={{ marginRight: '10px'}}/> +38 (096) 104 07 13</ContactItem>
        <ContactItem><EmailIcon style={{ marginRight: '10px'}}/> ivanzlatoff@gmail.com </ContactItem>
        <Payment src='https://i.ibb.co/Qfvn4z6/payment.png'/>
      </div>
    </div>
  )
}

export default Footer
