import styled from 'styled-components';
import Announcement from '../components/Announcement';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';
import Newsletter from '../components/Newsletter';

const Container = styled.div``;

const Wrapper = styled.div`
    padding: 50px;
    display: flex;
`;

const ImgContainer = styled.div`
    flex: 1;
`;


const Image = styled.img`
    width: 100%;
    height: 90vh;
    object-fit: cover;
`;

const InfoContainer = styled.div`
    flex: 1;
    padding: 0px 50px;
`;

const Title = styled.h1`
    font-weight: 200;
`;

const Desc = styled.p`
    margin: 20px 0px;
`;

const Price = styled.span`
    font-weight: 100;
    font-size: 40px;
`;

const FilterContainer = styled.div`
    display: flex;
    width: 50%;
    margin: 30px 0px;
    justify-content: space-between;
`;

const Filter = styled.div`
    display: flex;
    align-items: center;
    background-color: gray;
    padding: 10px;
`;

const FilterTitle = styled.span`
    font-size: 20px;
    font-weight: 200;
`;

const FilterColor = styled.div`
    width: 20px;
    height: 20px;
    border-radius: 50%;
    margin-left: 5px;
    background-color: ${props => props.color};
`;


const Product = () => {
  return (
    <Container>
      <Navbar />
      <Announcement />
      <Wrapper>
        <ImgContainer>
            <Image src={require(`../sources/images/isabella2.webp`)} />
        </ImgContainer>
        <InfoContainer>
            <Title>Isabella</Title>
            <Desc>Lorem ipsum, dolor sit amet consectetur adipisicing elit. Quaerat nisi vel at commodi, doloribus voluptatum eos enim quisquam aut delectus architecto. A at aspernatur enim perferendis? Temporibus officiis obcaecati quidem.</Desc>
            <Price>UAH 20 PER 1 KG</Price>
            <FilterContainer>
                <Filter>
                    <FilterTitle>Color</FilterTitle>
                    <FilterColor color='red'/>
                    <FilterColor color='white'/>
                </Filter>
            </FilterContainer>
        </InfoContainer>
      </Wrapper>
      <Newsletter />
      <Footer />
    </Container>
  )
}

export default Product
