import styled from 'styled-components';
import Announcement from '../components/Announcement';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import { mobile } from '../responsive';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { deleteProduct, clearWishlist } from '../redux/wishlistRedux';
import ClearIcon from '@mui/icons-material/Clear';
import { toast } from 'react-hot-toast';


const Container = styled.div``;

const Wrapper = styled.div`
    padding: 20px;
    ${mobile({ padding: '10px' })};
`;

const Title = styled.h1`
    font-weight: 300;
    text-align: center;
`;

const Top = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 20px;
`;

const TopButton = styled.button`
    padding: 10px;
    font-weight: 600;
    cursor: pointer;
    background-color: ${props => props.type === 'filled' ? 'black' : 'transparent'};
    color: ${props => props.type === 'filled' && 'white'}
`;

const TopTexts = styled.div`
    ${mobile({ display: 'none' })}
`;

const TopText = styled.span`
    text-decoration: underline;
    cursor: pointer;
    margin: 0px 10px;
`

const Bottom = styled.div`
    display: flex;
    justify-content: space-between;
    ${mobile({ flexDirection: 'column' })}
`;

const Info = styled.div`
    flex: 3
`;

const ProductContainer = styled.div`

`;

const Product = styled.div`
    display: flex;
    justify-content: space-between;
    ${mobile({ flexDirection: 'column' })}
`;

const ProductDetails = styled.div`
    flex: 2;
    display: flex;
`;

const Image = styled.img`
    width: 200px;
`;

const Details = styled.div`
    display: flex;
    padding: 20px;
    flex-direction: column;
    justify-content: space-around;
`;

const ProductName = styled.span``;

const UnitPrice = styled.span``;

const ProductId = styled.span``;

const PriceDetails = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`;

const Desc = styled.p`
    margin: 20px 0px;
`;

const Hr = styled.hr`
    background-color: #eee;
    border: none;
    height: 1px;
`;

const Wishlist = () => {
    const cart = useSelector(state => state.cart);
    const wishlist = useSelector(state => state.wishlist);
    const dispatch = useDispatch();
    console.log(wishlist)

    const handleDelete = (product) => {
        dispatch(
            deleteProduct({ ...product })
        );
        toast.error(`${product.title} of ${product.color} color removed from the wishlist.`);
    };

    const handleClearWishlist = () => {
        dispatch(
            clearWishlist()
        );
        toast.error('Your wishlist has been cleared!');
    }

  return (
    <Container>
      <Navbar />
      <Announcement />
      <Wrapper>
        <Title>YOUR WISHLIST</Title>
        <Top>
            <Link to="/">
                <TopButton>CONTINUE SHOPPING</TopButton>
            </Link>
            <TopTexts>
                <Link to="/cart">
                    <TopText>ShoppingBag ({cart.quantity})</TopText>
                </Link>
                <TopText>Your wishlist ({wishlist.quantity})</TopText>
            </TopTexts>
            <TopButton 
                type='filled' 
                style={{ backgroundColor: 'pink'}}
                onClick={() => handleClearWishlist()}
            >
                EMPTY MY WISHLIST
            </TopButton>
            <TopButton type='filled'>CHECK OUT NOW</TopButton>
        </Top>
        <Bottom>
            <Info>
                {wishlist.products?.map(product => ( 
                    <ProductContainer key={product._id}> 
                        <Product>
                            <ProductDetails>
                                <Image src={product.img} />
                                <Details>
                                    <ProductName><b>Product: </b>{product.title}</ProductName>
                                    <UnitPrice><b>Price: </b>UAH {product.price} PER KG</UnitPrice>
                                </Details>
                            </ProductDetails>
                            <Desc>{product.desc}</Desc>
                            <Link to={`/product/${product._id}`}>
                                Go to 
                            </Link>
                            <ClearIcon 
                                style={{ 
                                    color: 'grey', 
                                    marginRight: '10',
                                    cursor: 'pointer',
                                    "hover": {
                                        transform: 'scale(1.1)',
                                        color: 'red',
                                    }
                                }} 
                                onClick={() => handleDelete(product)}

                            />
                        </Product>
                           <ClearIcon 
                                style={{ 
                                    color: 'grey', 
                                    marginRight: '10',
                                    cursor: 'pointer',
                                    "hover": {
                                        transform: 'scale(1.1)',
                                        color: 'red',
                                    }
                                }} 
                                onClick={() => handleDelete(product)}

                            />
                        <Hr />
                    </ProductContainer> )
                    ) 
                }
            </Info>
        </Bottom>
      </Wrapper>
      <Footer />
    </Container>
  )
}

export default Wishlist
