import axios from 'axios';


const BASE_URL = 'http://localhost:5000/api/';

const TOKEN = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzYTg1MDUzMjU3NTljNDI4ZmIwNzExNyIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY3NjE0NjQ4OCwiZXhwIjoxNjc2MTQ2NDk4fQ.fLDMk4kntQ4Yd5XDjbzVltujELYZM94LkSEkVMXyDw0'
// JSON.parse(JSON.parse(localStorage?.getItem("persist:root"))?.user)?.currentUser?.accessToken;

console.log("TOKEN => ", TOKEN)

export const publicRequest = axios.create({
    baseURL: BASE_URL
});

export const userRequest = axios.create({
    baseURL: BASE_URL,
    headers: {token: `Bearer ${TOKEN}`},
});

export default TOKEN;
