import axios from "axios";
import { publicRequest, userRequest } from "../requestMethods";
import { clearCart, fetchCart } from "./cartRedux";
import { 
    loginFailure, 
    loginStart, 
    loginSuccess, 
    refreshTokenStart,
    refreshTokenSuccess,
    refreshTokenFailure,
    logoutStart, 
    logoutSuccess, 
    logoutFailure 
} from "./userRedux"
import { clearWishlist, fetchWishlist } from "./wishlistRedux";
import { toast } from 'react-hot-toast';

export const refreshAccessToken = async (dispatch, refreshToken, refreshTokenId) => {

    dispatch(refreshTokenStart());
    try {
        const res = await publicRequest.put("/auth/refresh_token", {refreshToken, refreshTokenId}, { 
            withCredentials: true
        });
        console.log("res.data => ", res.data)
        dispatch(refreshTokenSuccess(res.data));
        return res.data
    } catch(err) {
        dispatch(refreshTokenFailure());
    }
};

export const login = async (dispatch, user) => {
    dispatch(loginStart());
    try {
        const res = await publicRequest.post("/auth/login", user)
        console.log(res.data)
        dispatch(loginSuccess(res.data))
        window.location.reload(false)
    } catch(err) {
        console.log(err)
        dispatch(loginFailure());
        toast.error(err.response.data.error);
    }
};

export const logout = async (dispatch, user) => {
    dispatch(logoutStart());
    const { _id, refreshTokenId } = user;
    try {
        const res = await userRequest.delete("/auth/logout_post", {userId: _id, refreshTokenId});
        if (res) {
            dispatch(logoutSuccess());
            dispatch(clearCart());
            dispatch(clearWishlist());
            localStorage.clear();
        } 
    } catch(err) {
        console.log(err);
        dispatch(logoutFailure());
    }
};
