import { clearRefreshToken, publicRequest, userRequest } from "../requestMethods";
import { 
    loginFailure, 
    loginStart, 
    loginSuccess, 
    refreshTokenStart,
    refreshTokenSuccess,
    refreshTokenFailure,
    logoutStart, 
    logoutSuccess, 
    logoutFailure 
} from "./userRedux"
import { toast } from 'react-hot-toast';

export const refreshAccessToken = async (dispatch, refreshToken, refreshTokenId) => {

    dispatch(refreshTokenStart());
    try {
        const res = await publicRequest.put("/auth/refresh_token", {refreshToken, refreshTokenId}, { 
            withCredentials: true
        });
        dispatch(refreshTokenSuccess(res.data));
        return res.data
    } catch(err) {
        dispatch(refreshTokenFailure());
    }
};

export const login = async (dispatch, user) => {
    dispatch(loginStart());
    try {
        const res = await publicRequest.post("/auth/login", user);
        const { accessToken, refreshToken, refreshTokenId, ...others } = res.data;
        dispatch(loginSuccess(others))
    } catch(err) {
        console.log(err)
        dispatch(loginFailure());
        toast.error(err.response.data.error);
    }
};

export const logout = async (dispatch, user) => {
    dispatch(logoutStart());
    try {
        await userRequest.post("/auth/logout_post", user)
        dispatch(logoutSuccess());
        clearRefreshToken();
        delete userRequest.defaults.headers.common["token"]
    } catch(err) {
        dispatch(logoutFailure());
        console.log(err);
    }
};
