import styled from 'styled-components';
import i18n from "i18next";
import { initReactI18next, useTranslation } from "react-i18next";


let languageStrings = {
    en: {
        announcement: "Super Deal! Free Shipping on Orders Over UAH 5000",
    },
    ua: {
        announcement: "Супер пропозиція! Безкоштовна доставка при замовленні від 5000 грн",     
    }
}

i18n
    .use(initReactI18next) // passes i18n down to react-i18next
    .init({
        resources: {
            en: { translation: languageStrings.en },
            ua: { translation: languageStrings.ua }
        },
        lng: "en",
        fallbackLng: "en",
        interpolation: { escapeValue: false },
    })

const Container = styled.div`
    height: 30px;
    background-color: teal;
    color: white;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 18px;
    font-weight: 500;
`

const Announcement = () => {
  const { t } = useTranslation();
  return (
    <Container>
      {t("announcement")} 
    </Container>
  )
}

export default Announcement
