import { useState } from 'react';
import styled from 'styled-components';
import SearchIcon from '@mui/icons-material/Search';
import Badge from '@mui/material/Badge';
import { ShoppingCartOutlined, Favorite } from '@mui/icons-material/';
import { mobile, tablet } from '../responsive';
import { Link, useNavigate } from 'react-router-dom';
import { logout, refreshToken } from "../redux/apiCalls";
import { useDispatch, useSelector } from "react-redux";
import i18n from "i18next";
import { initReactI18next, useTranslation } from "react-i18next";


let languageStrings = {
    en: {
        language: "EN",
        search: "search",
        logo: "VINOGRADNIK BESSARABII",
        register: "REGISTER",
        login: "LOGIN",
        logout: "LOGOUT"
    },
    ua: {
        language: "UA",
        search: "пошук",
        logo: "ВИНОГРАДНИК БЕССАРАБІЇ",
        register: "ЗАРЕЄСТРУВАТИСЯ",
        login: "ВХІД",
        logout: "ВИЙТИ"       
    }
}

i18n
    .use(initReactI18next) // passes i18n down to react-i18next
    .init({
        resources: {
            en: { translation: languageStrings.en },
            ua: { translation: languageStrings.ua }
        },
        lng: "en",
        fallbackLng: "en",
        interpolation: { escapeValue: false },
    })




const Container = styled.div`
    height: 60px;
    margin-bottom: 10px;
`;

const Wrapper = styled.div`
    padding: 10px 20px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    ${tablet({ fontSize: '10px' })};
    ${mobile({ padding: '0' })};
`;

const Left = styled.div`
    flex: 1;
    display: flex;
    align-items: center;
`;

const Language = styled.select`
    font-size: 14px;
    cursor: pointer;
    ${mobile({ display: 'none' })}
`;

const Option = styled.option`
    background-color: skyblue;
    &:hover{
        background-color: teal;
    }
`

const SearchContainer = styled.div`
    border: 0.5px solid lightgray;
    display: flex;
    align-items: center;
    margin-left: 25px;
    padding: 5px;
`;

const Input = styled.input`
    border:none;
    padding: 5px;
    ${mobile({ width: '50px' })}
`;


const Center = styled.div`
    flex: 1;
    text-align: center;
`;

const Logo = styled.h1`
    font-weight: bold;
    ${mobile({ fontSize: '16px', marginLeft: '10px'})}
`;


const Right = styled.div`
    flex: 1;
    display: flex;
    align-items: center;
    justify-content: flex-end;
    ${mobile({ flex: '2', justifyContent: 'center'})}
`;


const MenuItem = styled.div`
    font-size: 14px;
    cursor: pointer;
    margin-left: 25px;
    ${mobile({ fontSize: '10px', marginLeft: '10px'})}
`;

const Navbar = () => {
    const cartQuantity = useSelector(state => state.cart.quantity);
    const wishlistQuantity = useSelector(state => state.wishlist.quantity);
    const user = useSelector(state => state.user.currentUser);
    const dispatch = useDispatch();
    const [search, setSearch] = useState('');
    const navigate = useNavigate();
    const { t } = useTranslation();

    const handleClick = (e) => {
        e.preventDefault();
        logout(dispatch, user);
        navigate("/");
    };

    const handleSearch = (e) => {
        e.preventDefault()
        setSearch(e.target.value)
    };

    const handleSearchClick = () => {
        search && navigate(`/product_search/${search}`)
    };

    const refresh = refreshToken();

    const handleLanguage = (e) => {
        i18n.changeLanguage(e.target.value)
    };

  return (
    <div>
        <Container>
            <Wrapper>
                <Left>
                    <Language onChange={handleLanguage}>
                        <Option value="en">EN</Option>
                        <Option value="ua">UA</Option>
                        <Option value="ru">RU</Option>
                    </Language>
                    <SearchContainer>
                        <Input placeholder={t("search")} onChange={handleSearch} /> 
                        <SearchIcon style={{ color: 'gray', fontSize: '16px' }} onClick={handleSearchClick} />
                    </SearchContainer>
                </Left>
                <Center>
                    <Logo>
                        <Link to="/" style={{ textDecoration: 'none' }}>{t("logo")}</Link>
                    </Logo>
                </Center>
                <Right>
                    {user 
                        ? <MenuItem onClick={(e) => handleClick(e)}>{t("logout")}</MenuItem>
                        :
                        <>
                            <Link to="/register">
                                <MenuItem>{t("register")}</MenuItem> 
                            </Link>
                            <Link to="/login">
                                <MenuItem>{t("login")}</MenuItem>
                            </Link>
                        </>
                    }
                    <Link to="/wishlist">
                        <MenuItem>
                            <Badge 
                                badgeContent={wishlistQuantity} 
                                color="primary"
                            >
                                <Favorite color="action" />
                            </Badge>
                        </MenuItem>
                    </Link>
                    <Link to="/cart">
                        <MenuItem>
                            <Badge 
                                badgeContent={cartQuantity} 
                                color="primary"
                            >
                                <ShoppingCartOutlined color="action" />
                            </Badge>
                        </MenuItem>
                    </Link>
                </Right>
            </Wrapper>
        </Container>
    </div>
  )
}

export default Navbar
