import { useLocation } from 'react-router-dom';
import styled from 'styled-components';
import Navbar from '../components/Navbar';
import Announcement from '../components/Announcement';
import Products from '../components/Products';
import Newsletter from '../components/Newsletter';
import Footer from '../components/Footer';
import { mobile } from '../responsive';
import { useState } from 'react';


const Container = styled.div``

const Title = styled.h1`
    margin: 20px;
`

const FilterContainer = styled.div`
    display: flex;
    justify-content: space-between;
`
const Filter = styled.div`
    margin: 20px;
    ${mobile({ margin: '0 20px', display: 'flex', flexDirection: 'column'})};
`

const FilterText = styled.span`
    font-size: 20px;
    font-weight: 600;
    margin-right: 20px;
    ${mobile({ margin: '0'})};
`

const Select = styled.select`
    padding: 10px;
    margin-right: 20px;
    ${mobile({ margin: '10px 0'})};
`

const Option = styled.option`

`;

const ProductList = () => {
    const location = useLocation();
    const category = location.pathname.split("/")[2];
    const [filters, setFilters] = useState({});
    const [sort, setSort] = useState('newest');

    const handleFilters = (e) => {
        const value = e.target.value;
        setFilters({
            ...filters,
            [e.target.name]: value.toLowerCase()
        });
    };

  return (
    <Container>
      <Navbar />
      <Announcement />
      <Title>{category[0].toUpperCase() + category.slice(1)}</Title>
      <FilterContainer>
        <Filter>
            <FilterText>Filter Products: </FilterText>
            <Select name='color' onChange={handleFilters} defaultValue='Color'>
                <Option disabled>
                    Color
                </Option>
                <Option>Red</Option>
                <Option>White</Option>
            </Select>
            <Select name='type' onChange={handleFilters} defaultValue='Type'>
                <Option disabled>
                    Type
                </Option>
                <Option>Isabella</Option>
                <Option>Odessky Cherny</Option>
                <Option>Merlot</Option>
                <Option>Kabernet</Option>
            </Select>
        </Filter>
        <Filter>
            <FilterText>Sort Products: </FilterText>
            <Select onChange={(e) => setSort(e.target.value)} defaultValue='newest'>
                <Option value='newest'>Newest</Option>
                <Option value='asc'>Price (asc)</Option>
                <Option value='desc'>Price (desc)</Option>
            </Select>
        </Filter>
      </FilterContainer>
      <Products category={category} filters={filters} sort={sort}/>
      <Newsletter />
      <Footer />
    </Container>
  )
}

export default ProductList
