import { useState } from "react";
import { useSelector } from "react-redux";
import "./contact.css";
import { useForm } from "react-hook-form";
import { publicRequest } from "../../requestMethods";


const Contact = () => {
  const [ inputs, setInputs ] = useState({});
  const user = useSelector(state => state.user.currentUser);
  const { handleSubmit, handleChange, formState: { errors } } = useForm();
  

  const onChange = (e) => {
    setInputs(prev => {
      return {...prev, [e.target.name]: e.target.value}
    })
  };

  const onSubmit = async (data) => {
    try {
      const res = await publicRequest.post("./notifications", {
        userId: user._id,
        username: user.username,
        email: inputs['email'],
        isAdmin: user.isAdmin,
        telephone: inputs['telephone'],
        subject: inputs['subject'],
        body: inputs['body'],
      })
    } catch(err) {
      console.log(err)
    }
  }

  return (
    <div className="container">
      <h1 className="title">Get in touch</h1>
      <form className="contactForm" onSubmit={handleSubmit(onSubmit)}>
        <div className="subject">
          <label>Subject</label>
          <input type="text" id="subject" placeholder="Subject" onChange={handleChange(onChange)}></input>
        </div>
        <div className="telephone">
          <label>Your telephone number</label>
        </div>
        <div className="message">
          <label>Your Message:</label>
          <textarea name="message" type="text" placeholder="Start typing..." onChange={handleChange(onChange)}></textarea>
        </div>
      </form>
    </div>
  )
}

export default Contact
