import styled from 'styled-components';
import Announcement from '../components/Announcement';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';


const Container = styled.div``;

const Wrapper = styled.div`
    padding: 20px;
`;

const Title = styled.h1`
    font-weight: 300;
    text-align: center;
`;

const Top = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 20px;
`;

const TopButton = styled.button`
    padding: 10px;
    font-weight: 600;
    cursor: pointer;
    border: ${props => props.type === 'filled' && 'none'};
    background-color: ${props => props.type === 'filled' ? 'black' : 'transparent'};
    color: ${props => props.type === 'filled' && 'white'}
`;

const TopTexts = styled.div`

`;

const TopText = styled.span`
    text-decoration: underline;
    cursor: pointer;
    margin: 0px 10px;
`

const Bottom = styled.div`
    display: flex;
    justify-content: space-between;
`;

const Info = styled.div`
    flex: 3
`;

const Product = styled.div`
    display: flex;
    justify-content: space-between;
`;

const ProductDetails = styled.div`
    flex: 2;
    display: flex;
`;

const Image = styled.img`
    width: 200px;
`;

const Details = styled.div`
    display: flex;
    padding: 20px;
    flex-direction: column;
    justify-content: space-around;
`;

const ProductName = styled.span``;

const ProductId = styled.span``;

const ProductColor = styled.div`
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background-color: #${props => props.color}
`;

const PriceDetails = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`;

const ProductAmountContainer = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 20px;
`;

const ProductAmount = styled.div`
    font-size: 24px;
    margin: 5px;
`;

const ProductPrice = styled.div`
    font-size: 30px;
    font-weight: 200;
`;

const Hr = styled.hr`
    background-color: #eee;
    border: none;
    height: 1px;
`;

const Summary = styled.div`
    flex: 1;
`;

const SummaryTitle = styled.h1``;
const SummaryItem = styled.div``;
const SummaryItemText = styled.span``;
const SummaryItemPrice = styled.span``;
const SummaryButton = styled.button``;

const Cart = () => {
  return (
    <Container>
      <Navbar />
      <Announcement />
      <Wrapper>
        <Title>YOUR CART</Title>
        <Top>
            <TopButton>CONTINUE SHOPPING</TopButton>
            <TopTexts>
                <TopText>ShoppingBag (2)</TopText>
                <TopText>Your wishlist (0)</TopText>
            </TopTexts>
            <TopButton type='filled'>CHECK OUT NOW</TopButton>
        </Top>
        <Bottom>
            <Info>
                <Product>
                    <ProductDetails>
                        <Image src={require(`../sources/images/220px-Isabella_grape.webp`)} />
                        <Details>
                            <ProductName><b>Product: </b>ISABELLA</ProductName>
                            <ProductId><b>ID: </b>54535345343</ProductId>
                            <ProductColor color='6f2da8' />
                        </Details>
                    </ProductDetails>
                    <PriceDetails>
                        <ProductAmountContainer>
                            <AddIcon />
                            <ProductAmount>2</ProductAmount>
                            <RemoveIcon />
                        </ProductAmountContainer>
                        <ProductPrice>UAH 20 PER KG</ProductPrice>
                    </PriceDetails>
                </Product>
                <Hr />
                <Product>
                    <ProductDetails>
                        <Image src={require(`../sources/images/220px-Isabella_grape.webp`)} />
                        <Details>
                            <ProductName><b>Product: </b>ISABELLA</ProductName>
                            <ProductId><b>ID: </b>54535345343</ProductId>
                            <ProductColor color='6f2da8' />
                        </Details>
                    </ProductDetails>
                    <PriceDetails>
                        <ProductAmountContainer>
                            <AddIcon />
                            <ProductAmount>2</ProductAmount>
                            <RemoveIcon />
                        </ProductAmountContainer>
                        <ProductPrice>UAH 20 PER KG</ProductPrice>
                    </PriceDetails>
                </Product>
            </Info>
            <Summary>
                <SummaryTitle>ORDER SUMMARY</SummaryTitle>
                <SummaryItem>
                    <SummaryItemText>Subtotal</SummaryItemText>
                    <SummaryItemText>UAH 2000</SummaryItemText>
                </SummaryItem>
                <SummaryItem>
                    <SummaryItemText>Estimated Shipping</SummaryItemText>
                    <SummaryItemText>UAH 600</SummaryItemText>
                </SummaryItem>
                <SummaryItem>
                    <SummaryItemText>Shipping Discount</SummaryItemText>
                    <SummaryItemText>UAH -100</SummaryItemText>
                </SummaryItem>
                <SummaryItem>
                    <SummaryItemText type='totla'>Total</SummaryItemText>
                    <SummaryItemText>UAH 2500</SummaryItemText>
                </SummaryItem>
                <SummaryButton>CHECKOUT NOW</SummaryButton>
            </Summary>
        </Bottom>
      </Wrapper>
      <Footer />
    </Container>
  )
}

export default Cart
