import { useState } from "react";
import { useSelector } from "react-redux";
import "./contact.css";
import { useForm } from "react-hook-form";
import { publicRequest } from "../../requestMethods";


const Contact = () => {
  const [ inputs, setInputs ] = useState({});
  const user = useSelector(state => state.user.currentUser);
  const { register, handleSubmit, formState: { errors } } = useForm();
  
  console.log(inputs)

  const handleChange = (e) => {
    setInputs(prev => {
      return {...prev, [e.target.name]: e.target.value}
    })
  };

  const onSubmit = async (data) => {
    try {
      const res = await publicRequest.post("./notifications", {
        userId: user._id,
        username: user.username,
        email: inputs['email'] || user.email,
        isAdmin: user.isAdmin,
        title: inputs['personTitle'],
        firstName: inputs['firstName'],
        lastName: inputs['lastName'],
        telephone: inputs['telephone'],
        subject: inputs['subject'],
        body: inputs['body'],
      });
      setInputs({});
    } catch(err) {
      console.log(err)
    }
  };

  return (
    <div className="container">
      <form className="contactForm" onSubmit={handleSubmit(onSubmit)}>
        <h1 className="messageTitle">Get in touch</h1>
        <div className="formField">
          <label htmlFor="personTitle">Title</label>
          <select 
            {...register('personTitle', {
              required: 'This is required'
            })}
            name="personTitle" 
            onChange={handleChange}
            defaultValue={'default'}
          >
            <option value="default" disabled>Choose here</option>
            <option value="Mr">Mr</option>
            <option value="Mrs">Mrs</option>
            <option value="Miss">Miss</option>
            <option value="Ms">Ms</option>
          </select>
          {errors.personTitle && <p style={{ color: 'red' }}>Please check the title</p>}
        </div>
        <div className="formField">
          <label htmlFor="firstName">First Name</label>
          <input 
            {...register('firstName', { 
              maxLength: 20,
              required: 'This is required'
            })}
            name="firstName"
            value={inputs['firstName']}
            type="text" 
            id="firstName" 
            placeholder="John" 
            onChange={handleChange} 
          />
          {errors.firstName && <p style={{ color: 'red' }}>Please check the first name</p>}
        </div>
        <div className="formField">
          <label htmlFor="lastName">Last Name</label>
          <input 
            {...register('lastName', { 
              maxLength: 20,
              required: 'This is required'
            })}
            name="lastName"
            value={inputs['lastName']}
            type="text" 
            id="lastName" 
            placeholder="Doe" 
            onChange={handleChange} 
          />
          {errors.lastName && <p style={{ color: 'red' }}>Please check the last name</p>}
        </div>
        <div className="formField">
          <label htmlFor="telephone">Your telephone number</label>
          <input 
            {...register('telephone', {
              pattern: /^[+]?(8|38)?(\-|\s)?0(50|63|66|67|68|91|92|93|96|97|98|99)(\-|\s)?((\d{3}(\-|\s)?\d{2}(\-|\s)?\d{2}(\-|\s)?)|(\d{2}(\-|\s)?\d{3}(\-|\s)?\d{2}(\-|\s)?)|(\d{2}(\-|\s)?\d{2}(\-|\s)?\d{3}(\-|\s)?))$/
            })}
            name="telephone"
            value={inputs['telephone']}
            type="tel"
            id="telephone"
            placeholder="+38-098-1234567"
            inputMode="numeric"
            autoComplete="tel"
            onChange={handleChange}
          />
          {errors.telephone && <p style={{ color: 'red' }}>Please check your telephone number {`${errors.telephone}`}</p>}
        </div>
        <div className="formField">
            <label htmlFor="email">Email</label>
            <input 
              {...register('email', {
                required: 'This is required',
                pattern: /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
              })}
              name="email"
              value={inputs['email']}
              placeholder='example@email.com'
              onChange={handleChange}
            />
        </div>
        <div className="formField">
          <label htmlFor="subject">Subject</label>
          <input 
            {...register('subject', {
              required: 'This is required'
            })}
            name="subject"
            type="text" 
            id="subject" 
            placeholder="Subject" 
            onChange={handleChange} 
          />
          {errors.subject && <p style={{ color: 'red' }}>Please enter the subject</p>}
        </div>
        <div className="formField">
          <label htmlFor="message">Your Message:</label>
          <textarea 
            {...register('message', {
              required: 'This is required'
            })}
            name="message" 
            type="text" 
            placeholder="Start typing..." 
            onChange={handleChange} 
          />
          {errors.message && <p style={{ color: 'red' }}>Please enter your message</p>}
        </div>
        <button type='submit'>Submit</button>
      </form>
    </div>
  )
}

export default Contact
