import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import jwtDecode from 'jwt-decode';
import { publicRequest } from '../requestMethods';
import { logout } from './apiCalls';


export const refreshAccessToken = createAsyncThunk('auth/refreshAccessToken', async (refreshToken, { dispatch }) => {
    try {
        const res = await publicRequest.post('auth/refresh_token', { refreshToken });
        const { accessToken } = res.data;
        return accessToken;
    } catch(err) {
        dispatch(logout());
        throw err;
    }
});

const refreshTokenMiddleware = ({ dispatch, getState }) => next => action => {
    next(action);
    const state = getState();
    const { accessToken, refreshToken } = state.auth;

    if (!accessToken || !refreshToken) {
        return;
    }

    const expirationTime = jwtDecode(accessToken).exp;

    if (Date.now() >= expirationTime * 1000) {
        dispatch(refreshAccessToken(refreshToken));
    }
}


const userSlice = createSlice({
    name: 'user',
    initialState: {
        currentUser: null,
        isFetching: false,
        error: false
    },
    reducers: {
        loginStart: (state) => {
            state.isFetching = true
            state.error = false
        },
        loginSuccess: (state, action) => {
            state.isFetching = false;
            state.currentUser = action.payload
        },
        loginFailure: (state) => {
            state.isFetching = false;
            state.error = true;
        },
        // refreshTokenStart: (state) => {
        //     state.isFetching = true
        // },
        // refreshTokenSuccess: (state, action) => {
        //     state.isFetching = false;
        //     state.currentUser.accessToken = action.payload.accessToken;
        //     state.currentUser.refreshToken = action.payload.refreshToken;
        // },
        // refreshTokenFailure: (state) => {
        //     state.isFetching = false;
        //     state.error = true;
        // },
        logoutStart: (state) => {
            state.isFetching = true
        },
        logoutSuccess: (state) => {
            state.isFetching = false;
            state.currentUser = null;
            state.error = false;
        },
        logoutFailure: (state) => {
            state.isFetching = false;
            state.error = true;
        }
    },
    extraReducers: {
        [refreshAccessToken.fulfilled]: (state, action) => {
            state.accessToken = action.payload;
        },
    },
});

export const { 
    loginStart, 
    loginSuccess, 
    loginFailure, 
    refreshTokenStart,
    refreshTokenSuccess,
    refreshTokenFailure,
    logoutStart, 
    logoutSuccess, 
    logoutFailure 
} = userSlice.actions;
export default userSlice.reducer;
