import { useState } from "react";
import { useSelector } from "react-redux";
import "./contact.css";
import { useForm } from "react-hook-form";
import { publicRequest } from "../../requestMethods";


const Contact = () => {
  const [ inputs, setInputs ] = useState({});
  const user = useSelector(state => state.user.currentUser);
  const { register, handleSubmit, formState: { errors } } = useForm();
  

  const handleChange = (e) => {
    setInputs(prev => {
      return {...prev, [e.target.name]: e.target.value}
    })
  };

  const onSubmit = async (data) => {
    try {
      const res = await publicRequest.post("./notifications", {
        userId: user._id,
        username: user.username,
        email: inputs['email'],
        isAdmin: user.isAdmin,
        title: inputs['personTitle'],
        firstName: inputs['firstName'],
        lastName: inputs['lastName'],
        telephone: inputs['telephone'],
        subject: inputs['subject'],
        body: inputs['body'],
      })
    } catch(err) {
      console.log(err)
    }
  }

  return (
    <div className="container">
      <h1 className="messageTitle">Get in touch</h1>
      <form className="contactForm" onSubmit={handleSubmit(onSubmit)}>
        <div className="personTitle">
          <lable>Title</lable>
          <select 
            {...register('personTitle')}
            name="personTitle" 
            onChange={handleChange}
          >
            <option value="Mr">Mr</option>
            <option value="Mrs">Mrs</option>
            <option value="Miss">Miss</option>
            <option value="Ms">Ms</option>
          </select>
          {errors.personTitle && <p style={{ color: 'red' }}>Please check the title</p>}
        </div>
        <div className="firstName">
          <label>First Name</label>
          <input 
            {...register('firstName', { maxLength: 20 })}
            name="firstName"
            type="text" 
            id="firstName" 
            placeholder="John" 
            onChange={handleChange} 
          />
          {errors.firstName && <p style={{ color: 'red' }}>Please check the first name</p>}
        </div>
        <div className="lastName">
          <label>Last Name</label>
          <input 
            {...register('lastName', { maxLength: 20 })}
            name="lastName"
            type="text" 
            id="lastName" 
            placeholder="Doe" 
            onChange={handleChange} 
          />
          {errors.lastName && <p style={{ color: 'red' }}>Please check the last name</p>}
        </div>
        <div className="telephone">
          <label>Your telephone number</label>
        </div>
        <div className="subject">
          <label>Subject</label>
          <input type="text" id="subject" placeholder="Subject" onChange={handleChange} />
        </div>
        <div className="message">
          <label>Your Message:</label>
          <textarea name="message" type="text" placeholder="Start typing..." onChange={handleChange} />
        </div>
      </form>
    </div>
  )
}

export default Contact
