import { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import ShoppingCartCheckoutIcon from '@mui/icons-material/ShoppingCartCheckout';
import { clearCart } from '../redux/cartRedux';
import { useDispatch, useSelector } from 'react-redux';
import { runFireworks } from '../lib/utils';
import styled from 'styled-components';
import { userRequest } from "../requestMethods";


const Container = styled.div`
  background-color: white;
  min-height: 60vh;
`;

const Wrapper = styled.div`
  width: 1000px;
  margin: auto;
  margin-top: 160px;
  background-color: #dcdcdc;
  padding: 50px;
  border-radius: 15px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const Icon = styled.p`
   color: green;
   font-size: 40px; 
`;

const ThankYou = styled.h2`
  text-transform: capitalize;
  margin-top: 15px 0px;
  font-weight: 900;
  font-size: 40px;
  color:#324d67;
`;

const EmailMsg = styled.p`
  font-size: 16px;
  font-weight: 600;
  text-align: center;
`;

const Desc = styled.p`
  font-size: 16px;
  font-weight: 600;
  text-align: center;
  margin: 10px;
  margin-top: 30px;
`;

const EmailTo = styled.a`
  margin-left: 5px;
  color: #f02d34;
`;

const Button = styled.button`
    padding: 10px;
    font-weight: 600;
    cursor: pointer;
    background-color: black;
    color: white;
`;

const Success = () => {
  const location = useLocation();
  const data = location.state.stripeData;
  const cart = location.state.cart;
  const currentUser = useSelector((state) => state.user.currentUser);
  const [orderId, setOrderId] = useState(null);
  const dispatch = useDispatch();
    
    useEffect(() => {
      const createOrder = async () => {
        try {
          const res = await userRequest.post("/orders", {
            userId: currentUser._id,
            products: cart.products.map((item) => ({
              productId: item._id,
              quantity: item._quantity,
            })),
            amount: cart.totalPrice,
            address: data.billing_details.address,
          });
          setOrderId(res.data._id);
        } catch(err) {
          console.log(err);
        };
        data && createOrder();
      }
        localStorage.clear();
        dispatch(
          clearCart()
        );
        runFireworks();
    }, [cart, data, currentUser]);

  return (
    <Container>
      <Wrapper>
          <Icon>
            <ShoppingCartCheckoutIcon />
          </Icon>
          <ThankYou>Thank you for your order!</ThankYou>
          <EmailMsg>Check your email inbox for the receipt.</EmailMsg>
          <Desc>
              If you have any questions, please email
              <EmailTo href="mailto:ivanzlatoff@gmail.com">
                ivanzlatoff@gmail.com
              </EmailTo>  
          </Desc>
          {orderId
            ? `Order has been created successfully. Your order number is ${orderId}`
            : `Successfull. Your order is being prepared...`}
          <Link to="/">
              <Button>
                  Go To Home Page To Continue Shopping
              </Button>
          </Link>
      </Wrapper>
    </Container>
  )
}

export default Success
