import styled from 'styled-components';
import Announcement from '../components/Announcement';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import { mobile } from '../responsive';
import { useSelector } from 'react-redux';
import StripeCheckout from 'react-stripe-checkout';
import { useEffect, useState } from 'react';
import { userRequest } from '../requestMethods';
import { useNavigate, Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { increaseProduct } from '../redux/cartRedux';
import { toast } from 'react-hot-toast';

const KEY = process.env.REACT_APP_STRIPE;

const Container = styled.div``;

const Wrapper = styled.div`
    padding: 20px;
    ${mobile({ padding: '10px' })};
`;

const Title = styled.h1`
    font-weight: 300;
    text-align: center;
`;

const Top = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 20px;
`;

const TopButton = styled.button`
    padding: 10px;
    font-weight: 600;
    cursor: pointer;
    background-color: ${props => props.type === 'filled' ? 'black' : 'transparent'};
    color: ${props => props.type === 'filled' && 'white'}
`;

const TopTexts = styled.div`
    ${mobile({ display: 'none' })}
`;

const TopText = styled.span`
    text-decoration: underline;
    cursor: pointer;
    margin: 0px 10px;
`

const Bottom = styled.div`
    display: flex;
    justify-content: space-between;
    ${mobile({ flexDirection: 'column' })}
`;

const Info = styled.div`
    flex: 3
`;

const ProductContainer = styled.div`

`;

const Product = styled.div`
    display: flex;
    justify-content: space-between;
    ${mobile({ flexDirection: 'column' })}
`;

const ProductDetails = styled.div`
    flex: 2;
    display: flex;
`;

const Image = styled.img`
    width: 200px;
`;

const Details = styled.div`
    display: flex;
    padding: 20px;
    flex-direction: column;
    justify-content: space-around;
`;

const ProductName = styled.span``;

const ProductId = styled.span``;

const ProductColor = styled.div`
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background-color: #${props => props.color.toLowerCase() === 'red' 
                            ? '6f2da8'
                            : props.color.toLowerCase() === 'white'
                            ? 'A6BE47'
                            : 'white'
                        };
`;

const PriceDetails = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`;

const ProductAmountContainer = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 20px;
`;

const ProductAmount = styled.div`
    font-size: 24px;
    margin: 5px;
    ${mobile({ margin: '5px 15px' })}
`;

const ProductPrice = styled.div`
    font-size: 30px;
    font-weight: 200;
    ${mobile({ marginBottom: '20px' })}
`;

const Hr = styled.hr`
    background-color: #eee;
    border: none;
    height: 1px;
`;

const Summary = styled.div`
    flex: 1;
    border: 0.5px solid lightgray;
    border-radius: 10px;
    padding: 20px;
    height: 60%;
`;

const SummaryTitle = styled.h1`
    font-weight: 200;
`;

const SummaryItem = styled.div`
    margin: 30px 0px;
    display: flex;
    justify-content: space-between;
    font-weight: ${props => props.type === 'total' && '500'};
    font-size: ${props => props.type === 'total' && '24px'};
`;

const SummaryItemText = styled.span``;

const SummaryButton = styled.button`
    width: 100%;
    padding: 10px;
    background-color: black;
    color: white;
    font-weight: 200;
    font-size: 18px;
    cursor: pointer;
`;

const Cart = () => {
    const cart = useSelector(state => state.cart);
    const [stripeToken, setStripeToken] = useState(null);
    const [quantity, setQuantity] = useState(null);
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const onToken = (token) => {
        setStripeToken(token);
    };

    useEffect(() => {
        stripeToken && cart.totalPrice >= 1 &&
        (async () => {
            try {
                const res = await userRequest.post('/checkout/payment', {
                    tokenId: stripeToken.id,
                    amount: cart.totalPrice * 100,
                });
                navigate('/success', { 
                    stripeData: res.data,
                    products: cart,
                });
            } catch {}
        })();
    }, [stripeToken, cart.totalPrice, navigate])

    const handleQuantity = (type, product) => {
        if (type === 'inc') {
            dispatch(
                increaseProduct(product)
            );
        } else {
            // dispatch(
            //     decreaseProduct({ ...product, quantity, color})
            // );
        }
    };

  return (
    <Container>
      <Navbar />
      <Announcement />
      <Wrapper>
        <Title>YOUR CART</Title>
        <Top>
            <Link to="/">
                <TopButton>CONTINUE SHOPPING</TopButton>
            </Link>
            <TopTexts>
                <TopText>ShoppingBag (2)</TopText>
                <TopText>Your wishlist (0)</TopText>
            </TopTexts>
            <TopButton type='filled'>CHECK OUT NOW</TopButton>
        </Top>
        <Bottom>
            <Info>
                {cart.products.map(product => ( 
                    <ProductContainer key={product._id}> 
                        <Product>
                            <ProductDetails>
                                <Image src={product.img} />
                                <Details>
                                    <ProductName><b>Product: </b>{product.title}</ProductName>
                                    <ProductId><b>ID: </b>{product._id}</ProductId>
                                    <ProductColor color={product.color} />
                                </Details>
                            </ProductDetails>
                            <PriceDetails>
                                <ProductAmountContainer>
                                    <RemoveIcon onClick={() => handleQuantity('dec', ...product) } style={{ color: 'red' }} />
                                    <ProductAmount>{product.quantity} kg</ProductAmount>
                                    <AddIcon onClick={() => handleQuantity('inc') } style={{ color: 'green' }} />
                                </ProductAmountContainer>
                                <ProductPrice>UAH {product.price * product.quantity}</ProductPrice>
                            </PriceDetails>
                        </Product>
                        <Hr />
                    </ProductContainer> )
                    ) 
                }
            </Info>
            <Summary>
                <SummaryTitle>ORDER SUMMARY</SummaryTitle>
                <SummaryItem>
                    <SummaryItemText>Subtotal</SummaryItemText>
                    <SummaryItemText>UAH {cart.totalPrice}</SummaryItemText>
                </SummaryItem>
                <SummaryItem>
                    <SummaryItemText>Estimated Shipping</SummaryItemText>
                    <SummaryItemText>UAH 600</SummaryItemText>
                </SummaryItem>
                <SummaryItem>
                    <SummaryItemText>Shipping Discount</SummaryItemText>
                    <SummaryItemText>UAH -100</SummaryItemText>
                </SummaryItem>
                <SummaryItem type='total'>
                    <SummaryItemText>Total</SummaryItemText>
                    <SummaryItemText>UAH {cart.totalPrice}</SummaryItemText>
                </SummaryItem>
                <StripeCheckout
                    name='Vinogradnik Bessarabii'
                    // image={require('../sources/images/grape-icon.png')}
                    billingAddress
                    shippingAddress
                    description={`Your total is UAH ${cart.totalPrice}`}
                    amount={cart.total*100}
                    token={onToken}
                    stripeKey={KEY}
                >
                    <SummaryButton>CHECKOUT NOW</SummaryButton>
                </StripeCheckout>
            </Summary>
        </Bottom>
      </Wrapper>
      <Footer />
    </Container>
  )
}

export default Cart
