import { useNavigate, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import Announcement from '../components/Announcement';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';
import Newsletter from '../components/Newsletter';
import RemoveIcon from '@mui/icons-material/Remove';
import AddIcon from '@mui/icons-material/Add';
import { mobile, tablet } from '../responsive';
import { useEffect, useState } from 'react';
import { publicRequest } from '../requestMethods';
import { addProduct } from '../redux/cartRedux';
import { useDispatch } from 'react-redux';
import { toast } from 'react-hot-toast';
import { useTranslation } from 'react-i18next';


const Container = styled.div``;

const Wrapper = styled.div`
    padding: 50px;
    display: flex;
    ${mobile({ padding: '10px', flexDirection: 'column' })};
    ${tablet({ padding: '10px', flexDirection: 'column' })};
`;

const ImgContainer = styled.div`
    flex: 1;
`;


const Image = styled.img`
    width: 100%;
    height: 90vh;
    object-fit: cover;
    ${tablet({ height: '60vh', flexDirection: 'column' })};
    ${mobile({ height: '40vh', flexDirection: 'column' })};
`;

const InfoContainer = styled.div`
    flex: 1;
    padding: 0px 50px;
    text-align: center;
    ${mobile({ padding: '10px' })}
`;

const MainInfo = styled.div`
    display: flex;
    text-align: start;
`;

const LeftInfo = styled.div`
    flex: 1;
`;

const Title = styled.h1`
    font-weight: 200;
    text-align: start;
`;

const Desc = styled.p`
    flex: 2;
    margin: 20px 0px;
`;

const Price = styled.span`
    font-weight: 100;
    font-size: 40px;
`;

const FilterContainer = styled.div`
    width: 100%;
    margin: 30px 0px;
    justify-content: space-between;
    align-items: center;
    ${mobile({ width: '100%' })}
`;

const Filter = styled.div`
    display: flex;
    align-items: center;
    padding: 10px;
`;

const FilterTitle = styled.span`
    font-size: 20px;
    font-weight: 200;
`;

const FilterColor = styled.span`
    width: 20px;
    height: 20px;
    border-radius: 50%;
    margin: 0 5px;
    background-color: #${props => props.color.toLowerCase() === 'red' 
                            ? '6f2da8'
                            : props.color.toLowerCase() === 'white'
                            ? 'A6BE47'
                            : 'white'
                        };
    cursor: pointer;
`;

const SelectedColor = styled.div`
    font-size: 20px;
    font-weight: 200;
`;

const AddContainer = styled.div`
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    justify-content: space-between;
    ${mobile({ width: '100%' })}
`;

const AmountContainer = styled.div`
    display: flex;
    align-items: center;
    font-weight: 700;
`;

const Amount = styled.span`
    width: 100px;
    border: 1px solid teal;
    align-items: center;
    justify-content: center;
    margin: 0 10px;
    padding: 14px;
    text-align: center;
    font-weight: 500;
`;

const AddToCartButton = styled.button`
    padding: 14px;
    width: 20vw;
    margin-top: 0 5px;
    border: 1px solid teal;
    background-color: white;
    color: teal;
    cursor: pointer;
    font-size: 18px;
    font-weight: 500;
    &:hover {
        background-color: #f8f4f4;
    }
`;

const BuyNowButton = styled.button`
    padding: 14px;
    background-color: teal;
    color: white;
    width: 100%;
    margin-left: auto;
    margin-right: auto;
    margin-top: 20px;
    cursor: pointer;
    font-weight: 500;
    font-size: 18px;
    &:hover {
        background-color: #4e8773;
    }
`;

const Product = () => {
    const location = useLocation();
    const id = location.pathname.split("/")[2];
    const [product, setProduct] = useState({});
    const [quantity, setQuantity] = useState(100);
    const [color, setColor] = useState('red');
    const dispatch = useDispatch();
    const [isLoading, setIsLoading] = useState(true);
    const navigate = useNavigate();
    const { t } = useTranslation(["product"]);

    useEffect(() => {
        (async () => {
            try {
                setIsLoading(true);
                const res = await publicRequest.get('/products/find/' + id)
                setProduct(res.data)
            } catch(err) {
                console.log(err);
            } finally {
                setIsLoading(false);
            }
        })();
    }, [id]);

    const handleQuantity = (type) => {
        if (type === 'dec') {
            quantity > 100 && setQuantity(quantity - 100)
        } else {
            quantity < 10000 && setQuantity(quantity + 100)
        }
    };

    const handleColor = (color) => {
        setColor(color);
    }

    const handleClick = () => {
        dispatch(
            addProduct({ ...product, quantity, color })
        );
        toast.success(`${quantity} kg of ${product.title} added to the cart.`);
        setQuantity(100)
    };

    const handleBuy = () => {
        dispatch(
            addProduct({ ...product, quantity, color })
        );
        navigate("/cart")
    }
  return (
    <Container>
      <Navbar />
      <Announcement />
      {isLoading 
        ? 'Loading, please wait...' 
        : (<Wrapper>
            <ImgContainer>
                <Image src={product.img} />
            </ImgContainer>
            <InfoContainer>
                <MainInfo>
                    <LeftInfo>
                        <Title>{product.title}</Title>
                        <Price>{product.price} {t("currency")} {t("per_kg")}</Price>
                    </LeftInfo>
                    <Desc>{product.desc}</Desc>
                </MainInfo>
                <FilterContainer>
                    <Filter>
                        <FilterTitle>{t("choose_color")}</FilterTitle>
                        {product.color?.map((color) => (
                            <FilterColor color={color} key={color} onClick={() => handleColor(color)}/>                            
                        ))}
                    </Filter>
                    <Filter>
                        <SelectedColor>{t("selected_color")} {color}</SelectedColor>
                        <FilterColor color={color} />
                    </Filter>
                </FilterContainer>
                <AddContainer>
                    <AmountContainer>
                        <RemoveIcon onClick={() => handleQuantity('dec') } style={{ color: 'red' }} />
                        <Amount>{quantity} {t("kg")}</Amount>
                        <AddIcon onClick={() => handleQuantity('inc') } style={{ color: 'green' }} />
                    </AmountContainer>
                <AddToCartButton onClick={handleClick}>{t("add_to_cart")}</AddToCartButton>
                </AddContainer>
                <BuyNowButton onClick={handleBuy}>{t("buy_now")}</BuyNowButton>
            </InfoContainer>
        </Wrapper>)}
      <Newsletter />
      <Footer />
    </Container>
  )
}

export default Product
