import axios from "axios";
import { publicRequest, userRequest } from "../requestMethods";
import { clearCart } from "./cartRedux";
import { 
    loginFailure, 
    loginStart, 
    loginSuccess, 
    refreshTokenStart,
    refreshTokenSuccess,
    refreshTokenFailure,
    logoutStart, 
    logoutSuccess, 
    logoutFailure 
} from "./userRedux"
import { clearWishlist } from "./wishlistRedux";

export const refreshToken = async (dispatch, user) => {
    dispatch(refreshTokenStart())
    try {
        const res = await axios.post("/refresh_token", { token: user.refreshToken });
        dispatch(refreshTokenSuccess(res.data));
        return res.data
    } catch(err) {
        dispatch(refreshTokenFailure());
    }
};

export const login = async (dispatch, user) => {
    dispatch(loginStart());
    try {
        const res = await publicRequest.post("/auth/login", user)
        dispatch(loginSuccess(res.data))
    } catch(err) {
        dispatch(loginFailure());
    }
};

export const logout = async (dispatch, user) => {
    dispatch(logoutStart());
    try {
        const res = await userRequest.delete("/auth/logout", user);
        dispatch(logoutSuccess(res));
        dispatch(clearCart());
        dispatch(clearWishlist());
    } catch(err) {
        console.log(err);
        dispatch(logoutFailure());
    }
};
