import { categories_en, categories_ua } from '../data';
import styled from 'styled-components';
import CategoryItem from './CategoryItem';


const Container = styled.div`
  display: flex;
  padding: 20px;
  justify-content: space-between;
  flex-wrap: wrap;  
`;

const Categories = () => {
  const lang = localStorage.getItem("i18nextLng");

  return (
    <Container>
      {lang === 'ua' 
        ? categories_ua.map(item => (<CategoryItem item={item} key={item.id}/>))
        : categories_en.map(item => (<CategoryItem item={item} key={item.id}/>))}
    </Container>
  )
}

export default Categories
