import styled from 'styled-components';


const Container = styled.div`

`

const SortItem = ({item}) => {
  return (
    <Container>
        <Image src={require(`../sources/images/${item.img}`)}/>
        <Info>
            <Title>{item.title}</Title>
            <Button>SHOP NOW</Button>
        </Info>
    </Container>
  )
}

export default SortItem
