import { createSlice } from '@reduxjs/toolkit';


const userSlice = createSlice({
    name: 'user',
    initialState: {
        currentUser: null,
        isFetching: false,
        error: false
    },
    reducers: {
        loginStart: (state) => {
            state.isFetching = true
            state.error = false
        },
        loginSuccess: (state, action) => {
            state.isFetching = false;
            state.currentUser = action.payload
        },
        loginFailure: (state) => {
            state.isFetching = false;
            state.error = true;
        },
        refreshTokenStart: (state) => {
            state.isFetching = true
        },
        refreshTokenSuccess: (state, action) => {
            state.isFetching = false;
            state.currentUser.accessToken = action.payload.accessToken;
        },
        refreshTokenFailure: (state) => {
            state.isFetching = false;
            state.error = true;
        },
        logoutStart: (state) => {
            state.isFetching = true
        },
        logoutSuccess: (state) => {
            state.isFetching = false;
            state.currentUser = null;
            state.error = false;
        },
        logoutFailure: (state) => {
            state.isFetching = false;
            state.error = true;
        }
    },
});

export const { 
    loginStart, 
    loginSuccess, 
    loginFailure, 
    refreshTokenStart,
    refreshTokenSuccess,
    refreshTokenFailure,
    logoutStart, 
    logoutSuccess, 
    logoutFailure 
} = userSlice.actions;
export default userSlice.reducer;
