import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate 
} from "react-router-dom";
import './App.css';
import Home from './pages/Home';
import ProductList from './pages/ProductList';
import Product from './pages/Product';
import Register from './pages/Register';
import Login from './pages/Login';
import Cart from './pages/Cart';
import Wishlist from './pages/Wishlist';
import Search from './pages/Search';
import Success from './pages/Success';
import { Toaster } from 'react-hot-toast';
import { useDispatch, useSelector } from "react-redux";
import React, { Suspense, useEffect } from 'react';
import { fetchCart } from "./redux/cartRedux";
import { fetchWishlist } from "./redux/wishlistRedux";
import Contact from "./components/contact/Contact";
import AuthVerify from "./lib/AuthVerify";
import { logout, refreshAccessToken } from "./redux/apiCalls";
import { useCallback } from "react";
import { userRequest } from "./requestMethods";
import jwt_decode from "jwt-decode";
import TOKEN from "./requestMethods";


const App = () => {
  const user = useSelector(state => state.user.currentUser);
  const userId = user?._id;
  const cartStatus = useSelector(state => state.cart.status);
  const wishlistStatus = useSelector(state => state.wishlist.status);
  const dispatch = useDispatch();

  const logOut = useCallback(() => {
    dispatch(logout());
  }, [dispatch]);

  useEffect(() => {
      if (TOKEN && userId && cartStatus === 'idle') {
          dispatch(fetchCart(userId));
      }
  }, [userId, cartStatus, dispatch]);

  useEffect(() => {
      if (TOKEN && userId && wishlistStatus === 'idle') {
          dispatch(fetchWishlist(userId));
      }
  }, [userId, wishlistStatus, dispatch]);

userRequest.interceptors.request.use(async (config) => {
  const accessToken = config.headers.token?.substring(7);
  let currentDate = new Date();
  const decodedToken = accessToken && jwt_decode(accessToken);
  if (decodedToken.exp * 1000 < currentDate.getTime()) {
    const data = await refreshAccessToken(dispatch, user.refreshToken, user.refreshTokenId)
    config.headers["token"] = "Bearer " + data.newAccessToken;  
    console.log("data => ", data)
  }
  return config;
}, (error) => {
  return Promise.reject(error)
});

  return (
    <Suspense fallback={<div>Loading...</div>}>
      <Router>
        <Routes>
          <Route exact path="/" element={<Home />} />
          <Route path="/products/:category" element={<ProductList />} />
          <Route path="/product/:id" element={<Product />} />
          <Route path="/product_search/:type" element={<Search />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/wishlist" element={<Wishlist />} />
          <Route path="/success" element={<Success />} />
          <Route path="/login" element={user ? <Navigate  to="/" /> : <Login />} />
          <Route path="/register" element={user ? <Navigate  to="/" /> : <Register />} />
          <Route path="/logout" element={<Navigate to="/" />} />
          <Route path="/contact_us" element={<Contact />} />
        </Routes>
        <Toaster />
        <AuthVerify logOut={logOut}/>
      </Router>
    </Suspense>    
  );
};

export default App;
