import React from 'react';
import styled from 'styled-components';
import SearchIcon from '@mui/icons-material/Search';
import Badge from '@mui/material/Badge';
import { makeStyles } from '@mui/material/core/styles';
import ShoppingCartOutlinedIcon from '@mui/icons-material/ShoppingCartOutlined';
import { mobile, tablet } from '../responsive';
import { useSelector } from 'react-redux';


const Container = styled.div`
    height: 60px;
    margin-bottom: 10px;
`;

const Wrapper = styled.div`
    padding: 10px 20px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    ${tablet({ fontSize: '10px' })};
    ${mobile({ padding: '0' })};
`;

const Left = styled.div`
    flex: 1;
    display: flex;
    align-items: center;
`;

const Language = styled.span`
    font-size: 14px;
    cursor: pointer;
    ${mobile({ display: 'none' })}
`;

const SearchContainer = styled.div`
    border: 0.5px solid lightgray;
    display: flex;
    align-items: center;
    margin-left: 25px;
    padding: 5px;
`;

const Input = styled.input`
    border:none;
    ${mobile({ width: '50px' })}
`;


const Center = styled.div`
    flex: 1;
    text-align: center;
`;

const Logo = styled.h1`
    font-weight: bold;
    ${mobile({ fontSize: '16px', marginLeft: '10px'})}
`;


const Right = styled.div`
    flex: 1;
    display: flex;
    align-items: center;
    justify-content: flex-end;
    ${mobile({ flex: '2', justifyContent: 'center'})}
`;


const MenuItem = styled.div`
    font-size: 14px;
    cursor: pointer;
    margin-left: 25px;
    ${mobile({ fontSize: '10px', marginLeft: '10px'})}
`;

const useStyles = makeStyles((theme) => ({
    badge: {
        fontSize: 10
    }
}))
 
const Navbar = () => {
    const classes = useStyles();
    const quantity = useSelector(state => state.cart.quantity)
  return (
    <div>
        <Container>
            <Wrapper>
                <Left>
                    <Language>EN</Language>
                    <SearchContainer>
                        <Input placeholder='search' /> 
                        <SearchIcon style={{ color: 'gray', fontSize: '16px' }} />
                    </SearchContainer>
                </Left>
                <Center><Logo>VINOGRADNIK BESSARABII</Logo></Center>
                <Right>
                    <MenuItem>REGISTER</MenuItem>
                    <MenuItem>SIGN IN</MenuItem>
                    <MenuItem>
                        <Badge badgeContent={quantity} color="primary" classes={{ badge: classes.badge}}>
                            <ShoppingCartOutlinedIcon color="action" />
                        </Badge>
                    </MenuItem>
                </Right>
            </Wrapper>
        </Container>
    </div>
  )
}

export default Navbar
