import { useState, useEffect } from 'react';
import styled from 'styled-components';
import SearchIcon from '@mui/icons-material/Search';
import Badge from '@mui/material/Badge';
import { ShoppingCartOutlined, Favorite } from '@mui/icons-material/';
import { mobile, tablet } from '../responsive';
import { Link, useNavigate } from 'react-router-dom';
import { logout } from "../redux/apiCalls";
import { useDispatch, useSelector } from "react-redux";
import { useTranslation } from "react-i18next";
import i18next from "i18next";
import { userRequest } from '../requestMethods';
import { useCreateCartMutation, useUpdateCartMutation } from '../redux/apiSlice';


const Container = styled.div`
    height: 60px;
    margin-bottom: 60px;
`;

const Wrapper = styled.div`
    padding: 10px 20px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    ${tablet({ fontSize: '10px' })};
    ${mobile({ padding: '0' })};
`;

const Left = styled.div`
    flex: 1;
    display: flex;
    align-items: center;
`;

const Language = styled.select`
    font-size: 14px;
    border: 0.5px solid lightgray;
    padding: 8px;
    cursor: pointer;
    ${mobile({ display: 'none' })}
`;

const Option = styled.option`
    background-color: skyblue;
    &:hover{
        background-color: teal;
    }
`

const SearchContainer = styled.div`
    border: 0.5px solid lightgray;
    display: flex;
    align-items: center;
    margin-left: 25px;
    padding: 5px;
`;

const Input = styled.input`
    border:none;
    padding: 5px;
    ${mobile({ width: '50px' })}
`;


const Center = styled.div`
    flex: 1;
    text-align: center;
`;

const Logo = styled.h1`
    font-weight: bold;
    ${mobile({ fontSize: '16px', marginLeft: '10px'})}
`;


const Right = styled.div`
    flex: 1;
    display: flex;
    align-items: center;
    justify-content: flex-end;
    ${mobile({ flex: '2', justifyContent: 'center'})}
`;


const MenuItem = styled.div`
    font-size: 14px;
    cursor: pointer;
    margin-left: 25px;
    ${mobile({ fontSize: '10px', marginLeft: '10px'})}
`;

const Navbar = () => {
    const cartQuantity = useSelector(state => state.cart.quantity);
    const cartId = useSelector(state => state.cart.id);
    const cartProducts = useSelector(state => state.cart.products);
    const createdAt = useSelector(state => state.cart.createdAt);
    const wishlistQuantity = useSelector(state => state.wishlist.quantity);
    const wishlistId = useSelector(state => state.wishlist.id);
    const wishlistProducts = useSelector(state => state.wishlist.products);
    const user = useSelector(state => state.user.currentUser);
    const dispatch = useDispatch();
    const [search, setSearch] = useState('');
    const navigate = useNavigate();
    const { i18n, t } = useTranslation(["navbar"]);

    const [updatedCart, { isLoading }] = useUpdateCartMutation();

    const handleClick = async (e) => {
        e.preventDefault();
        try { 
            if (cartQuantity && !cartId) {
                await userRequest.post('carts', {
                    userId: user._id, 
                    products: cartProducts,
                    createdAt: new Date(),
                    updatedAt: new Date()                   
                });            
            };
            if (cartQuantity && cartId) {
                await userRequest.put(`carts/${cartId}`, {
                    userId: user._id, 
                    products: cartProducts,
                    createdAt,
                    updatedAt: new Date()                   
                });
            };
            if (!cartQuantity && cartId) {
                await userRequest.delete(`carts/${cartId}`)
            }
        } catch(err) {
            console.log(err);
        }
        try {

            if (wishlistQuantity && !wishlistId) {
                await userRequest.post('wishlists', {
                    userId: user._id, 
                    products: wishlistProducts,
                    createdAt: new Date(),
                    updatedAt: new Date()                   
                });            
            };
            if (wishlistQuantity && wishlistId) {
                await userRequest.put(`wishlists/${wishlistId}`, {
                    userId: user._id, 
                    products: wishlistProducts,
                    createdAt,
                    updatedAt: new Date()                   
                });
            };
            if (!wishlistQuantity && wishlistId) {
                await userRequest.delete(`wishlists/${wishlistId}`)
            }
        } catch(err) {
            console.log(err);
        };
        
        logout(dispatch, user);
        navigate("/");
    };

    const handleSearch = (e) => {
        e.preventDefault()
        setSearch(e.target.value)
    };

    const handleSearchClick = () => {
        search && navigate(`/product_search/${search}`)
    };

    const handleLanguage = (e) => {
        i18n.changeLanguage(e.target.value)
    };

    useEffect(() => {
        if (localStorage.getItem("i18nextLng")?.length > 2) {
            i18next.changeLanguage("en");
        }
    }, []);

  return (
    <div>
        <Container>
            <Wrapper>
                <Left>
                    <Language  
                        onChange={handleLanguage} 
                        value={localStorage.getItem("i18nextLng")}
                    >
                        <Option value="en">English</Option>
                        <Option value="ua">Українська</Option>
                        <Option value="ru">Русский</Option>
                    </Language>
                    <SearchContainer>
                        <Input placeholder={t("search")} onChange={handleSearch} /> 
                        <SearchIcon style={{ color: 'gray', fontSize: '16px' }} onClick={handleSearchClick} />
                    </SearchContainer>
                </Left>
                <Center>
                    <Logo>
                        <Link to="/" style={{ textDecoration: 'none' }}>{t("logo")}</Link>
                    </Logo>
                </Center>
                <Right>
                    {user 
                        ? <MenuItem onClick={(e) => handleClick(e)}>{t("logout")}</MenuItem>
                        :
                        <>
                            <Link to="/register">
                                <MenuItem>{t("register")}</MenuItem> 
                            </Link>
                            <Link to="/login">
                                <MenuItem>{t("login")}</MenuItem>
                            </Link>
                        </>
                    }
                    <Link to="/wishlist">
                        <MenuItem>
                            <Badge 
                                badgeContent={wishlistQuantity} 
                                color="primary"
                            >
                                <Favorite color="action" />
                            </Badge>
                        </MenuItem>
                    </Link>
                    <Link to="/cart">
                        <MenuItem>
                            <Badge 
                                badgeContent={cartQuantity} 
                                color="primary"
                            >
                                <ShoppingCartOutlined color="action" />
                            </Badge>
                        </MenuItem>
                    </Link>
                </Right>
            </Wrapper>
        </Container>
    </div>
  )
}

export default Navbar
