import { useState } from 'react';
import validator from "validator";
import styled from "styled-components";
import { mobile } from "../responsive";
import { Link } from 'react-router-dom';
import { publicRequest } from '../requestMethods';
import { useForm } from "react-hook-form";

const Container = styled.div`
    width: 100vw;
    height: 100vh;
    background-image:  linear-gradient(
        rgba(255, 255, 255, 0.5), 
        rgba(255, 255, 255, 0.5)
        ),
        url("https://firebasestorage.googleapis.com/v0/b/shop-80015.appspot.com/o/grape-icon.png?alt=media&token=edb90e89-ed75-4e1d-b172-d92fdaba92bb");
    background-repeat: no-repeat;
    background-size: contain;
    background-position: 50% 0;
    background-color: lightgray;
    display: flex;
    align-items: center;
    justify-content: center;
`;

const Wrapper = styled.div`
    width: 40%;
    padding: 20px;
    background-color: lightgray;
    opacity: 0.9;
    text-align: center;
    ${mobile({ width: '75%'})};
`;
const Form = styled.form`
    display: flex;
    flex-direction: column;
    justify-content: center;
`;

const Title = styled.h1`
    font-size: 24ps;
    font-weight: 300;
`;

const Input = styled.input`
    flex: 1;
    min-width: 40%;
    margin: 20px 10px 0 0;
    padding: 10px;
`;

const Agrrement = styled.span`
    font-size: 12px;
    margin: 20px 0px;
`;

const Button = styled.button`
    width: 40%;
    border: none;
    padding: 15px 20px;
    background-color: teal;
    color: white;
    cursor: pointer;
    &:hover {
        background-color: #4e8773;
    };
    &:disabled {
        cursor: not-allowed;
    }
`;

const Div = styled.div`
    margin-top: 15px;
`

const Register = () => {
    const [name, setName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [passwordConfirm, setPasswordConfirm] = useState('');
    const [passwordMatch, setPasswordMatch] = useState(false);
    const { register, handleSubmit, watch, formState: { errors } } = useForm();

    const confirmPassword = (e) => {
        e.preventDefault()
        setPasswordConfirm(e.target.value)
        if (password === passwordConfirm) {
            setPasswordMatch(true);
        } else {
            setPasswordMatch(false);
        }
    };

    console.log(passwordMatch);

    const handleClick = async () => {

        try {
            const res = await publicRequest.post("/auth/register", {
                username: username,
                email: email,
                password: password,
            });
            console.log(res)
            setName('');
            setLastName('');
            setEmail('');
            setUsername('');
            setPassword('');
            setPasswordConfirm('');
        } catch(err) {
            console.log(err)
        }
    };

    const onSubmit = (data) => {
        console.log(data);
    }

  return (
    <Container>
      <Wrapper>
        <Title>CREATE AN ACCOUNT</Title>
        <Form onSubmit={handleSubmit(onSubmit)}>
            <Input
                {...register('name', { maxLength: 20 })} 
                type="text"
                name='name'
                value={name}
                placeholder='name'
                onChange={(e) => setName(e.target.value)}
            />
            {errors.name && <p style={{ color: 'red' }}>Please check the First Name</p>}
            <Input
                {...register('lastName', { maxLength: 20 })} 
                type="text"
                name='lastName'
                value={lastName}
                placeholder='last name'
                onChange={(e) => setLastName(e.target.value)} 
            />
            {errors.lastName && <p style={{ color: 'red' }}>Please check the Last Name</p>}
            <Input
                {...register('email', 
                { 
                    required: 'This is required',
                    pattern: /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/ 
                })} 
                // type="email"
                name='email'
                value={email}
                placeholder='email'
                onChange={(e) => setEmail(e.target.value)} 
            />
            {errors.email && <p style={{ color: 'red' }}>Please check the Email</p>}
            <Input
                {...register('username', { 
                    required: 'This is required',
                })} 
                type="text"
                name='username'
                value={username}
                placeholder='username'
                onChange={(e) => setUsername(e.target.value)} 
            />
            <Input
                {...register('password', { 
                    required: 'This is required',
                    pattern: /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,15}$/
                })} 
                type="password"
                name='password'
                value={password}
                placeholder='password'
                onChange={(e) => setPassword(e.target.value)} 
            />
            {errors.email && <p style={{ color: 'red' }}>Please check the Password. It should contain one Capital Letter, one Small Letter, and the number of characters should be between 6 to 15</p>}
            <Input
                {...register('passwordConfirm', {
                    required: 'This is required',
                    pattern: /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,15}$/
                })} 
                type="password"
                name='passwordConfirm'
                value={passwordConfirm}
                placeholder='confirm password'
                onChange={(e) => confirmPassword(e)} 
            />
            {passwordMatch 
                ? password && <Div style={{ color: 'green' }}>Passwords match!</Div>
                : password && <Div style={{ color: 'red' }}>Passwords do not match!</Div>}
            <Agrrement>
                By creating an account, I consent to the processing of my personal data in accordance with the <b>PRIVACY POLICY</b>
            </Agrrement>
            <Button type='submit'>CREATE</Button>
        </Form>
            <Div>
                Already have an account?
                <Link 
                    to="/login" 
                    style={{ marginLeft: 10 }}
                >
                    Sign in
                </Link>
            </Div>
      </Wrapper>
    </Container>
  )
}

export default Register
