import React, { useEffect } from 'react';
import Link from 'next/link';
import { BsBagCheckFill } from 'react-icons/bs';
import { clearCart } from '../redux/cartRedux';
import { useDispatch } from 'react-redux';
import { runFireworks } from '../lib/utils';
import styled from 'styled-components';


const Container = styled.div`
  background-color: white;
  min-height: 60vh;
`;

const Wrapper = styled.div`
  width: 1000px;
  margin: auto;
  margin-top: 160px;
  background-color: #dcdcdc;
  padding: 50px;
  border-radius: 15px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const Icon = styled.p`
   color: green;
   font-size: 40px; 
`;

const ThankYou = styled.h2`
  text-transform: capitalize;
  margin-top: 15px 0px;
  font-weight: 900;
  font-size: 40px;
  color:#324d67;
`;

const EmailMsg = styled.p`
  font-size: 16px;
  font-weight: 600;
  text-align: center;
`;

const Desc = styled.p`
  font-size: 16px;
  font-weight: 600;
  text-align: center;
  margin: 10px;
  margin-top: 30px;
`;

const EmailTo = styled.a`
  margin-left: 5px;
  color: #f02d34;
`;

const Button = styled.button`
    padding: 10px;
    font-weight: 600;
    cursor: pointer;
    background-color: ${props => props.type === 'filled' ? 'black' : 'transparent'};
    color: ${props => props.type === 'filled' && 'white'}
`;

const Success = () => {
    const dispatch = useDispatch();
    
    useEffect(() => {
        localStorage.clear();
        dispatch(
          clearCart()
        );
        runFireworks();
    }, []);

  return (
    <Container>
      <Wrapper>
          <Icon>
            <BsBagCheckFill />
          </Icon>
          <ThankYou>Thank you for your order!</ThankYou>
          <EmailMsg>Check your email inbox for the receipt.</EmailMsg>
          <Desc>
              If you have any questions, please email
              <EmailTo href="mailto:ivanzlatoff@gmail.com">
                ivanzlatoff@gmail.com
              </EmailTo>  
          </Desc>
          <Link to="/">
              <Button>
                  Continue Shopping
              </Button>
          </Link>
      </Wrapper>
    </Container>
  )
}

export default Success
